mapper.select:
select

from nk_store_master


		or ABBREV_OWNER_GROUP_NAME like #{abbrevOwnerGroupName}


		or STORE_SUB_TYPE = #{storeSubType}


		or CHINA_STORE_SUB_CHANNEL = #{chinaStoreSubChannel}



	limit #{start},#{size}


	limit #{number}

select count(*) from nk_store_master


		or ABBREV_OWNER_GROUP_NAME like #{abbrevOwnerGroupName}


		or STORE_SUB_TYPE = #{storeSubType}


		or CHINA_STORE_SUB_CHANNEL = #{chinaStoreSubChannel}
